import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { nanoid } from 'nanoid';

interface Group {
  _id: string;
  groupName: string;
  // other group properties
}

interface File {
  _id: string;
  filename: string;
  // other file properties
}

const UserHome: React.FC = () => {
  const [groups, setGroups] = useState<Group[]>([]);
  const [files, setFiles] = useState<File[]>([]);
  const [groupName, setGroupName] = useState('');

  useEffect(() => {
    // Fetch user's groups from the backend API
    const fetchGroups = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:8080/api/groups', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setGroups(response.data);
      } catch (error) {
        console.error('Error fetching groups:', error);
      }
    };

    // Fetch user's personal files from the backend API
    const fetchFiles = async () => {
      try {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user') || '{}');

        if (token && user._id) {
          const userId = user._id;
          const response = await axios.get(`http://localhost:8080/api/files/user/${userId}`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          setFiles(response.data);
        } else {
          console.error('User not authenticated');
        }
      } catch (error) {
        console.error('Error fetching files:', error);
      }
    };

    fetchGroups();
    fetchFiles();
  }, []);

  const handleCreateGroup = async () => {
    try {
      if (!groupName.trim()) {
        // Mostrar un mensaje de error al usuario indicando que el nombre del grupo es requerido
        console.error('Group name is required');
        return;
      }

      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const userId = user._id;

      const groupId = nanoid(); // Genera un identificador único más robusto

      const response = await axios.post(
        '/api/groups', // <-- Check the URL here
        {
          _id: groupId,
          groupName,
          members: [userId], // Add the current user as the initial member
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      console.log('Group created:', response.data);
      setGroups([...groups, response.data]); // Actualizar el estado de los grupos
      setGroupName(''); // Limpiar el campo de entrada después de crear el grupo
    } catch (error) {
      console.error('Error creating group:', error);
      // Mostrar un mensaje de error al usuario
    }
  };

  const updateGroups = (newGroup: Group) => {
    setGroups([...groups, newGroup]);
  };

  return (
    <div className="mt-16 p-4">
      <h1 className="text-2xl font-bold mb-4">Welcome to Your Dashboard</h1>
      <div className="mb-8">
        <h2 className="text-xl font-bold mb-2">Create a New Group</h2>
        <input
          type="text"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
          placeholder="Enter group name"
        />
        <button onClick={handleCreateGroup}>Create Group</button>
      </div>
      <div className="grid grid-cols-2 gap-8">
        <div>
          <h2 className="text-xl font-bold mb-2">Your Groups</h2>
          {/* Render the list of groups */}
          <ul className="space-y-2">
            {groups.map((group) => (
              <li key={group._id} className="text-gray-800">
                {group.groupName}
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h2 className="text-xl font-bold mb-2">Your Files</h2>
          {/* Render the list of personal files */}
          <ul className="space-y-2">
            {files.map((file) => (
              <li key={file._id} className="text-gray-800">
                {file.filename}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
